import Axios from "axios";
import { Form } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { useState } from "react";
import "./App.css"


function App() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const [employeeList, setEmployeeList] = useState([]); /*cus emp data is a list grammar ถูกป๊ะวะ*/

  const getEmployee = () => {
    Axios.get("http://localhost:5001/employees").then((response) => {
      setEmployeeList(response.data);
    })
  }

  return (
    <div className="App container">
      <h1>Employees Infomation</h1>
      <div className="information">
        <form action="">
          <div className="mb-3">
            <label className="form-label" htmlFor="name">
              Name:
            </label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter name"
              onChange={(event) => {
                setName(event.target.value)
              }}
            />
          </div>

          <button onClick={getEmployee} class="btn btn-success">
            Add Employee
          </button>
        </form>
      </div>
      <hr />
      <div className="employees">
        <button class="btn btn-primary" onClick={getEmployee}>
          Show Employees
        </button>
        <br />
        <br />
        {employeeList.map((val, key) => {
          return (
            <div className="employee card">
              <div className="card-body text-left">
                <p className="card-text">Name: {val.name}</p>
                <p className="card-text">Age: {val.age}</p>
                <p className="card-text">Country: {val.country}</p>
                <p className="card-text">Position: {val.position}</p>
                <p className="card-text">Wage: {val.wage}</p>
                <div className="d-flex">
                   </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
